mapper.select:
	SELECT * FROM (
	    SELECT ROWNUM rnum, tb.* FROM (
		     SELECT num, g.memberId, m.userName, g.blogId, g.content, g.created
		        FROM ${tableName}_guest g 
                JOIN member1 m ON g.memberId=m.memberId
                ORDER BY num DESC

        ) tb WHERE ROWNUM <= #{end}
    ) WHERE rnum >= #{start}
