mapper.select:
SELECT num, e.memberId, subject, content, created, isEvent
FROM event e JOIN member1 m ON e.memberId=m.memberId
WHERE num = #{num}

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT num, e.memberId, isEvent, subject, content, imageFilename, created
		FROM event e
		JOIN member1 m ON e.memberId=m.memberId





	    ORDER BY num DESC

	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
