mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM( SELECT num, f.memberId, subject, content, faqsort FROM faq f JOIN member1 m ON f.memberId = m.memberId
ORDER BY num DESC
) tb WHERE ROWNUM <= 10 ) WHERE rnum >= 1

SELECT num, f.memberId, subject, content, faqsort
FROM faq f JOIN member1 m ON f.memberId = m.memberId
WHERE num = #{num}
