mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT num, g.memberId, subject, content, created ,isGongji FROM gongji g JOIN member1 m ON g.memberId=m.memberId
ORDER BY num DESC
) tb WHERE ROWNUM <= 10 ) WHERE rnum >= 1

SELECT num, g.memberId, subject, content, created, isGongji
FROM gongji g JOIN member1 m ON g.memberId=m.memberId
WHERE num = #{num}
